"use strict";function myName(){var t=prompt("Напиши своє ім'я: ");alert("Привіт, ".concat(t))}function indicateDate(){var t=prompt("Напиши свій рік народження: "),r=(new Date).getFullYear()-t;alert("Твій вік, ".concat(r))}function perymetr(){var t=prompt("Вкажи сторону квадрата, а я тобі його периметр");alert("Периметр квадрата: ".concat(4*t))}function areaСircle(){var t=prompt("Вкажи радіус круга, а я тобі площу окружності"),r=Math.PI*t*t;alert("Площа окружності: ".concat(r))}function speed(){var t=prompt("Вкажи дистанцію між містами в км: "),r=prompt("Вкажи за скільки годин хочеш добратись: ");alert("Необхідна швидкість: ".concat(t/r," км/год"))}function converter(){var t=prompt("Вкажи суму в доларах а я тобі в євро: ");alert("Отримаєш ".concat(.85*t," євро"))}